import Services from '../../services/shortener.service'


export const shortenLink = (url, typeShorten) => {
   return dispatch => {
      Services.shortenLink(url)
         .then(res => dispatch({ type: "SUCCESS", payload: { data: res.data, typeShorten } }))
         .catch(err => console.log(err))
   }
}
